import React, { Component } from 'react';
import { Image, Platform, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { Header } from 'react-native-elements';
import { Left, Right, Icon, Container } from 'native-base';
import * as Permissions from 'expo-permissions';
import * as Location from 'expo-location';
import MapScreen from './Components/MapScreen.js';
//import HomeScreen from './WelcomeScreen.js';
//import WelcomeScreen from './Components/WelcomeScreen';
// import logo from './assets/logo.png';


const instructions = Platform.select({
  ios: 'Press Cmd+R to reload,\n' + 'Cmd+D or shake for dev menu',
  android: 'Double tap R on your keyboard to reload,\n' + 'Shake or press menu button for dev menu',
});


/*PAGES OF APP */
function WelcomeScreen({ navigation }) {
  return (
    <View style={styles.container}>
      <Header>
        <Left>
          <Icon style={{ color: "#ffffff" }} name="menu" onPress={() => navigation.openDrawer()} />
        </Left>
      </Header>

      <Text style={styles.heading}>COVID-19 Social Distancer</Text>

      <Text style={{ color: "#A00", fontSize: 20, padding: 5 }}>To get started, please catch COVID-19!</Text>

      <TouchableOpacity
        onPress={() => { navigation.navigate('MapScreen') }}
        style={styles.button}>
        <Text style={styles.buttonText}>BRAVE THE OUTSIDE</Text>
      </TouchableOpacity>
    </View>
  )
}

const Drawer = createDrawerNavigator();

// function myDrawer() {
//   return (
//     <Drawer.Navigator>
//       <Drawer.Screen name="Welcome" component={WelcomeScreen} />
//       <Drawer.Screen name="Maps" component={MapScreen} />
//     </Drawer.Navigator>
//   )
// }

export default class App extends Component {
  constructor(props) {
    super(props);
    this.setState({
      location: null,
      errorMessage: null,
    })
  }

  componentDidMount() {
    this._getLocation();
  }
  _getLocation = async () => {
    const { status } = await Permissions.askAsync(Permissions.LOCATION);

    if (status !== 'granted') {
      console.log('Permission Denied!');

      this.setState({
        errorMessage: 'Permission Denied!'
      })
    }

    const location = await Location.getCurrentPositionAsync();
    this.setState({
      location
    })
    console.log(JSON.stringify(this.state.location))
  }

  render() {
    // 

    // //Gets location from user
    // let text = "";
    // if (this.state.errorMessage) {
    //   text = this.state.errorMessage;
    // } else if (this.state.location) {
    //   text = JSON.stringify(this.state.location);
    //   console.log(text);
    // }

    // Side bar menu


    return (
      <NavigationContainer>
        <Drawer.Navigator initialRouteName="WelcomeScreen">
          <Drawer.Screen name="Welcome" component={WelcomeScreen} />
          <Drawer.Screen name="MapScreen" component={MapScreen} />
          {/* <Drawer.Screen name="Settings" component={SettingScreen}/> */}
          {/* <Drawer.Screen name="About" component={AboutScreen}/> */}
        </Drawer.Navigator>
      </NavigationContainer>


    );
  }
}
// const Drawer = createDrawerNavigator({

//   Home: {
//     screen: WelcomeScreen
//   },
//   Map: {
//     screen: MapScreen
//   }
// })


const styles = StyleSheet.create({
  outerContainer: {
    flex: 0.9, 
    justifyContent: "center", 
    alignItems: 'center', 
    paddingTop: 500,

  },
  container: {
    flex: 1,
    // justifyContent: 'center',
    alignItems: 'center',
    //alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  welcome: {
    fontSize: 20,
    textAlign: 'center',
    margin: 10,
  },
  instructions: {
    textAlign: 'center',
    color: '#333333',
    marginBottom: 5,
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  }
  ,
  buttonText: {
    fontSize: 20,
    color: "#fff",
  }
  ,
  thumbnail: {
    width: 300,
    height: 300,
    resizeMode: "contain",
  },
  heading: {
  //  flex: 0.90,
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    textAlign: 'center',
    margin: 5,
    fontSize: 40,
    fontWeight: 'bold'
  }
});
